html.script:
var active_warn = $('#active').val();
if (active_warn != "1") {
    msg_dom = $('#active_warn');
    msg_dom.show();
}
var review_active_warn = $('#review_active').val();
var config_id = $('#location_config_id').attr('value');
if (review_active_warn != "1" && config_id) {
    msg_dom = $('#review_active_warn');
    msg_dom.show();
}

set_unit_label = function(init) {
    if  ($('#_terp_editable').val() == 'True') {
        var unit = $('#time_unit_lt option:selected ').html();
        if (init) {
            $('#time_unit_lt').change(set_unit_label);
        }
    } else {
        var unit = $('#time_unit_lt').html().trim();
    }
    if (unit) {
        ['order_coverage', 'order_preparation_lt', 'order_creation_lt', 'order_validation_lt', 'supplier_lt', 'handling_lt', 'total_lt', 'internal_lt', 'external_lt'].forEach(function (f){
            var field_label = $('label[for='+f+']');
            if (field_label) {
                label_content = field_label.html().replace(/ *\([^)]*\) */g, "")
                field_label.html(label_content+ ' ('+unit+')');
            }
        });
    }
}
$(document).ready(function() {
    set_unit_label(true);
});

var safety_and_buffer = $('#safety_and_buffer_warn').val();
if (safety_and_buffer == "1") {
    msg_dom = $('#safety_and_buffer');
    msg_dom.show();
}
var active_warn = $('#active').val();
if (active_warn != "1") {
    msg_dom = $('#active_warn');
    msg_dom.show();
}
var review_active_warn = $('#review_active').val();
var config_id = $('#location_config_id').attr('value');
if (review_active_warn != "1" && config_id) {
    msg_dom = $('#review_active_warn');
    msg_dom.show();
}

new ListView('line_ids').after_reload = function() {
    var has_filter = $('#line_ids\\/_terp_domain').val();
    var nb_lines = $('#line_ids\\/_terp_count').val();
    if (has_filter != '[]' || nb_lines != "0") {
        $("#rule option:not(:selected)").attr("disabled", "disabled");
    } else {
        $("#rule option:not(:selected)").removeAttr("disabled");
    }
};

set_unit_label = function(init) {
    if  ($('#_terp_editable').val() == 'True') {
        var unit = $('#time_unit_lt option:selected ').html();
        if (init) {
            $('#time_unit_lt').change(set_unit_label);
        }
    } else {
        var unit = $('#time_unit_lt').html().trim();
    }
    if (unit) {
        ['order_coverage', 'safety_stock', 'order_preparation_lt', 'order_creation_lt', 'order_validation_lt', 'supplier_lt', 'handling_lt', 'total_lt', 'internal_lt', 'external_lt'].forEach(function (f){
            var field_label = $('label[for='+f+']');
            if (field_label) {
                label_content = field_label.html().replace(/ *\([^)]*\) */g, "")
                field_label.html(label_content+ ' ('+unit+')');
            }
        });
    }
}
$(document).ready(function() {
    set_unit_label(true);
});
var alert_msl_mml = $('#alert_msl_mml').val() || $('#alert_msl_mml').text();
if (alert_msl_mml) {
    $('#msl_mml').html(alert_msl_mml);
    $('#msl_mml').show();
}

set_label = function() {
    if  ($('#_terp_editable').val() == 'True') {
        var unit = $('#time_unit option:selected ').html();
    } else {
        var unit = $('#time_unit').html();
    }
    var amc_field = $('#grid-data-column\\/line_ids\\/unit_of_supply_amc');
    amc_field.html(unit+' '+amc_field.html());
    var fmc_field = $('#grid-data-column\\/line_ids\\/unit_of_supply_fmc')
    fmc_field.html(unit+' '+fmc_field.html());
}
new ListView('line_ids').after_reload = set_label;
set_label();
